mapper.insert:

INSERT INTO fnfmembertable(mphoto, midopen, mphoneopen, mjob, mintro)
VALUES(#{mphoto}, #{midopen}, #{mphoneopen}, #{mjob}, #{mintro})
WHERE bno = #{bno}

mapper.select:
SELECT * FROM (SELECT rownum rnum, a.* FROM (SELECT * FROM fafiboardtable WHERE bno > 0
AND bitem = #{bitem}
AND bitem = #{bitem}
AND btitle LIKE '%'||#{keyword}||'%'
AND bcontent LIKE '%'||#{keyword}||'%'
AND btitle LIKE '%'||#{keyword}||'%' OR bcontent LIKE '%'||#{keyword}||'%'
AND bwriter LIKE '%'||#{keyword}||'%'
ORDER BY bno DESC
ORDER BY bgoodcnt DESC, bno DESC
ORDER BY breplycnt DESC, bno DESC
ORDER BY bviewcnt DESC, bno DESC
) a ) WHERE rnum BETWEEN #{pageStart} AND #{perPageNum}

mapper.update:
	UPDATE fafiboardtable
	SET bviewcnt = bviewcnt + 1
    WHERE bno = #{bno}

choose.when:
UPDATE fafiboardtable SET btitle = #{btitle}, bwriter = #{bwriter}, bopen = #{bopen}, bitem = #{bitem}, bkind = #{bkind}, bpplace = #{bpplace}, bmainproduct = #{bmainproduct}, bproduct = #{bproduct}, btotalproduct = #{btotalproduct}, bpbirth = #{bpbirth}, bprice = #{bprice}, bsplace = #{bsplace}, bcontent = #{bcontent},
bfilename = #{bfilename}, bfilesize = #{bfilesize}
WHERE bno = #{bno}

choose.otherwise:
UPDATE fafiboardtable SET btitle = #{btitle}, bwriter = #{bwriter}, bopen = #{bopen}, bitem = #{bitem}, bkind = #{bkind}, bpplace = #{bpplace}, bmainproduct = #{bmainproduct}, bproduct = #{bproduct}, btotalproduct = #{btotalproduct}, bpbirth = #{bpbirth}, bprice = #{bprice}, bsplace = #{bsplace},
bfilename = #{bfilename}, bfilesize = #{bfilesize}
WHERE bno = #{bno}

mapper.select:
SELECT * FROM fnfreply
WHERE bno = #{bno}
ORDER BY rno DESC
